// Wine similarity calculator
const userQuery = $input.all()[0].json.query;
const wines = $input.all()[0].json.results || [];

// Calculate relevance scores
const scoredWines = wines.map(wine => {
  let score = wine.similarity_score || 0;

  // Boost score based on query keywords
  const queryLower = userQuery.toLowerCase();

  if (queryLower.includes('economico') && wine.price < 20) score += 0.2;
  if (queryLower.includes('pregiato') && wine.price > 50) score += 0.2;
  if (queryLower.includes('biologico') && wine.description?.includes('bio')) score += 0.3;
  if (queryLower.includes('abbinamento')) score += 0.1;

  // Price-quality ratio
  const priceQualityRatio = (100 - wine.price) / 100;
  score += priceQualityRatio * 0.1;

  return {
    ...wine,
    relevance_score: Math.min(score, 1.0),
    match_reasons: []
  };
});

// Sort by relevance
